mapper.insert:
INSERT INTO comment (
    content, user_id, post_id, parent_id, root_id, reply_user_id,
    like_count, status, created_at, updated_at
)
VALUES (
           #{content}, #{userId}, #{postId}, #{parentId}, #{rootId}, #{replyUserId},
           #{likeCount}, #{status}, #{createdAt}, #{updatedAt}
       )

mapper.update:
UPDATE comment
SET content = #{content},
    updated_at = #{updatedAt}
WHERE id = #{id}
  AND status = 1

mapper.select:
SELECT

FROM comment
WHERE user_id = #{userId}
AND status = 1
ORDER BY created_at DESC
LIMIT #{pageParam.offset}, #{pageParam.pageSize}
